import React, { useState } from "react";

function App() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    guess: "",
    code: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted Form Data:", formData);
  };

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center">
      <form
        onSubmit={handleSubmit}
        className="bg-zinc-900 p-8 rounded-xl shadow-md w-full max-w-md"
      >
        <h2 className="text-2xl font-bold mb-6 text-center text-[#92BC56]">
          Spidr Air Fryer Interest Form
        </h2>

        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={(e) =>
            setFormData({ ...formData, firstName: e.target.value })
          }
          className="w-full p-3 mb-4 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={(e) =>
            setFormData({ ...formData, lastName: e.target.value })
          }
          className="w-full p-3 mb-4 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <input
          type="tel"
          name="phoneNumber"
          placeholder="Phone Number"
          value={formData.phoneNumber}
          onChange={(e) => {
            let input = e.target.value.replace(/\D/g, "").slice(0, 10);
            const formatted = input.replace(
              /(\d{3})(\d{3})(\d{0,4})/,
              (_, a, b, c) => {
                return c ? `${a}-${b}-${c}` : b ? `${a}-${b}` : `${a}`;
              }
            );
            setFormData({ ...formData, phoneNumber: formatted });
          }}
          className="w-full p-3 mb-4 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={(e) =>
            setFormData({ ...formData, email: e.target.value })
          }
          className="w-full p-3 mb-4 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <input
          type="number"
          name="guess"
          placeholder="Guess the Air Fryerâ€™s Cost ($)"
          value={formData.guess}
          onChange={(e) =>
            setFormData({ ...formData, guess: e.target.value })
          }
          className="w-full p-3 mb-4 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <input
          type="text"
          name="code"
          placeholder="####-####-####-####"
          value={formData.code}
          onChange={(e) => {
            let input = e.target.value.replace(/\D/g, "").slice(0, 16);
            const formatted = input.match(/.{1,4}/g)?.join("-") || "";
            setFormData({ ...formData, code: formatted });
          }}
          className="w-full p-3 mb-6 rounded-md bg-black border border-zinc-700 focus:outline-none focus:ring-2 focus:ring-[#92BC56]"
        />

        <button
          type="submit"
          className="w-full bg-[#92BC56] hover:bg-[#7fae4b] text-black font-bold py-3 px-6 rounded-md transition duration-200"
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default App;
