import React, { useState, useEffect } from "react";
import "./index.css";

function App() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    guess: "",
    code: "",
    phoneNumber: "",
  });

  const [touched, setTouched] = useState({});
  const [showPopup, setShowPopup] = useState(false);

  // Load from localStorage on mount
  useEffect(() => {
    const saved = localStorage.getItem("spidrFormData");
    if (saved) {
      setFormData(JSON.parse(saved));
    }
  }, []);

  // Save to localStorage on any change
  useEffect(() => {
    localStorage.setItem("spidrFormData", JSON.stringify(formData));
  }, [formData]);

  const validateField = (field, value) => {
    switch (field) {
      case "firstName":
      case "lastName":
        return value.trim().length > 2;
      case "email":
        return /^\S+@\S+\.\S+$/.test(value);
      case "guess":
        return /^\d+$/.test(value);
      case "code":
        return /^\d{4}-\d{4}-\d{4}-\d{4}$/.test(value);
      case "phoneNumber":
        return /^\d{3}-\d{3}-\d{4}$/.test(value);
      default:
        return true;
    }
  };

  const isFormValid = () => {
    return Object.entries(formData).every(([key, value]) =>
      validateField(key, value)
    );
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleBlur = (e) => {
    const { name } = e.target;
    setTouched((prev) => ({ ...prev, [name]: true }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedTouched = Object.keys(formData).reduce((acc, key) => {
      acc[key] = true;
      return acc;
    }, {});
    setTouched(updatedTouched);

    if (!isFormValid()) {
      setShowPopup(true);
      return;
    }

    console.log("Submitted Form Data:", formData);
    localStorage.removeItem("spidrFormData"); // Clear saved progress
    alert("Form submitted!");
  };

  return (
    <div className="min-h-screen bg-black text-white p-6 flex justify-center items-center">
      <form
        onSubmit={handleSubmit}
        className="w-full max-w-md space-y-4 bg-[#0E0E0E] p-6 rounded-xl shadow-lg"
      >
        <h1 className="text-3xl font-bold text-center text-[#38AABF] mb-4">
          Spidr Air Fryer Interest Form
        </h1>

        {[
          { label: "First Name", name: "firstName" },
          { label: "Last Name", name: "lastName" },
          { label: "Email Address", name: "email", type: "email" },
          { label: "Guess the Air Fryer’s Cost ($)", name: "guess" },
          { label: "16-Digit Code", name: "code", placeholder: "####-####-####-####" },
          { label: "Phone Number", name: "phoneNumber", placeholder: "###-###-####" },
        ].map(({ label, name, type = "text", placeholder }) => {
          const value = formData[name];
          const showGreen = validateField(name, value);
          const showRed = touched[name] && !showGreen;

          return (
            <div key={name}>
              <label className="block mb-1 font-semibold">{label}</label>
              <input
                name={name}
                type={type}
                value={value}
                onChange={handleChange}
                onBlur={handleBlur}
                placeholder={placeholder}
                className={`w-full px-4 py-2 rounded-md bg-[#0E0E0E] border 
                  ${
                    showGreen
                      ? "border-green-600"
                      : showRed
                      ? "border-red-600"
                      : "border-gray-700"
                  } text-white placeholder-gray-500 focus:outline-none`}
              />
            </div>
          );
        })}

        <button
          type="submit"
          className="w-full mt-4 bg-[#38AABF] hover:bg-[#2e8ea0] text-white font-bold py-2 px-4 rounded-md"
        >
          Submit
        </button>
      </form>

      {showPopup && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-[#38AABF] text-white px-8 py-6 rounded-lg shadow-lg text-center">
            <p className="text-lg font-semibold mb-4">
              Please fix the highlighted fields.
            </p>
            <button
              onClick={() => setShowPopup(false)}
              className="bg-white text-[#38AABF] font-bold px-6 py-2 rounded-md"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
