import React, { useState } from 'react';
import './index.css';

const App = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    guess: '',
    code: '',
    phoneNumber: ''
  });

  const [touched, setTouched] = useState({});
  const [showPopup, setShowPopup] = useState(false);

  const validateField = (name, value) => {
    switch (name) {
      case 'firstName':
      case 'lastName':
        return value.length >= 3;
      case 'email':
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
      case 'guess':
        return /^\d+$/.test(value);
      case 'code':
        return /^\d{4}-\d{4}-\d{4}-\d{4}$/.test(value);
      case 'phoneNumber':
        return /^\d{3}-\d{3}-\d{4}$/.test(value);
      default:
        return true;
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    let formattedValue = value;

    if (name === 'code') {
      formattedValue = value
        .replace(/[^\d]/g, '')
        .slice(0, 16)
        .replace(/(.{4})/g, '$1-')
        .slice(0, 19)
        .replace(/-$/, '');
    }

    if (name === 'phoneNumber') {
      formattedValue = value
        .replace(/[^\d]/g, '')
        .slice(0, 10)
        .replace(/(\d{3})(\d{3})(\d{1,4})/, (match, p1, p2, p3) => {
          return `${p1}-${p2}-${p3}`;
        });
    }

    setFormData((prev) => ({ ...prev, [name]: formattedValue }));
    setTouched((prev) => ({ ...prev, [name]: true }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const invalidFields = Object.entries(formData).filter(
      ([name, value]) => !validateField(name, value)
    );

    if (invalidFields.length > 0) {
      setShowPopup(true);
      const invalidNames = invalidFields.map(([name]) => name);
      const updatedTouched = { ...touched };
      invalidNames.forEach((field) => (updatedTouched[field] = true));
      setTouched(updatedTouched);
      return;
    }

    console.log('Submitted Form Data:', formData);
  };

  const getBorderColor = (name) => {
    if (!touched[name]) return '';
    return validateField(name, formData[name]) ? 'border-green-600' : 'border-red-600';
  };

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <form
        onSubmit={handleSubmit}
        className="bg-black p-8 rounded-xl w-full max-w-md text-white shadow-lg"
      >
        <h1 className="text-3xl font-bold mb-6 text-center text-[#3FA6B3]">
          Spidr Air Fryer Interest Form
        </h1>

        {[
          { label: 'First Name', name: 'firstName' },
          { label: 'Last Name', name: 'lastName' },
          { label: 'Email Address', name: 'email' },
          { label: 'Guess the Air Fryer’s Cost ($)', name: 'guess', type: 'number' },
          { label: '16-Digit Code', name: 'code' },
          { label: 'Phone Number', name: 'phoneNumber' }
        ].map(({ label, name, type = 'text' }) => (
          <div key={name} className="mb-4">
            <label className="block text-sm font-semibold mb-1" htmlFor={name}>
              {label}
            </label>
            <input
              id={name}
              name={name}
              type={type}
              value={formData[name]}
              onChange={handleChange}
              className={`w-full p-3 rounded-md bg-black text-white placeholder-gray-400 focus:outline-none border ${getBorderColor(name)}`}
              placeholder={label}
            />
          </div>
        ))}

        <button
          type="submit"
          className="w-full mt-4 bg-[#3FA6B3] text-white font-semibold py-3 rounded-md hover:bg-[#3695A1] transition"
        >
          Submit
        </button>
      </form>

      {showPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-[#3FA6B3] text-white p-6 rounded-lg text-center w-80">
            <p className="mb-4 font-semibold text-lg">Please fix the highlighted fields.</p>
            <button
              onClick={() => setShowPopup(false)}
              className="bg-white text-[#3FA6B3] font-semibold px-6 py-2 rounded-md hover:bg-gray-100"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
