import React, { useState } from "react";

export default function App() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phoneNumber: "",
    email: "",
    guess: "",
    code: "",
  });

  const [touched, setTouched] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;

    let formattedValue = value;

    // Auto-format phone number: ###-###-####
    if (name === "phoneNumber") {
      formattedValue = value
        .replace(/\D/g, "")
        .slice(0, 10)
        .replace(/(\d{3})(\d{3})(\d{0,4})/, (_, a, b, c) =>
          c ? `${a}-${b}-${c}` : b ? `${a}-${b}` : `${a}`
        );
    }

    // Auto-format 16-digit code: ####-####-####-####
    if (name === "code") {
      formattedValue = value
        .replace(/\D/g, "")
        .slice(0, 16)
        .replace(/(\d{4})(\d{4})(\d{4})(\d{0,4})/, (_, a, b, c, d) =>
          d ? `${a}-${b}-${c}-${d}` : c ? `${a}-${b}-${c}` : b ? `${a}-${b}` : `${a}`
        );
    }

    setFormData((prev) => ({
      ...prev,
      [name]: formattedValue,
    }));

    setTouched((prev) => ({ ...prev, [name]: true }));
  };

  const validate = () => {
    return {
      firstName: formData.firstName.trim().length > 0,
      lastName: formData.lastName.trim().length > 0,
      phoneNumber: /^\d{3}-\d{3}-\d{4}$/.test(formData.phoneNumber),
      email: /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email),
      guess: formData.guess.trim().length > 0,
      code: /^\d{4}-\d{4}-\d{4}-\d{4}$/.test(formData.code),
    };
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validate();
    const hasError = Object.values(isValid).includes(false);

    if (hasError) {
      alert("Please fix the highlighted fields.");
      return;
    }

    console.log("Submitted Form Data:", formData);
  };

  const validity = validate();

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center px-4">
      <form
        onSubmit={handleSubmit}
        className="bg-[#1a1a1a] p-6 md:p-10 rounded-xl shadow-lg w-full max-w-md"
      >
        <h1 className="text-2xl md:text-3xl font-bold text-[#92BC56] mb-6 text-center">
          Spidr Air Fryer Interest Form
        </h1>

        {[
          { label: "First Name", name: "firstName", type: "text" },
          { label: "Last Name", name: "lastName", type: "text" },
          { label: "Phone Number", name: "phoneNumber", type: "text", placeholder: "123-456-7890" },
          { label: "Email Address", name: "email", type: "email" },
          { label: "Guess the Air Fryer’s Cost ($)", name: "guess", type: "number" },
          { label: "16-Digit Code", name: "code", type: "text", placeholder: "####-####-####-####" },
        ].map(({ label, name, type, placeholder }) => (
          <div key={name} className="mb-4">
            <label htmlFor={name} className="block text-sm mb-1 font-semibold">
              {label}
            </label>
            <input
              type={type}
              id={name}
              name={name}
              value={formData[name]}
              onChange={handleChange}
              placeholder={placeholder}
              className={`w-full px-4 py-2 rounded-md text-black focus:outline-none border transition-all duration-200 ${
                !touched[name]
                  ? "border-gray-300"
                  : validity[name]
                  ? "border-green-500"
                  : "border-red-500"
              }`}
            />
          </div>
        ))}

        <button
          type="submit"
          className="w-full mt-4 bg-[#92BC56] hover:bg-[#7aa43d] text-black font-bold py-2 px-4 rounded transition"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
