import { useState } from "react";

export default function App() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    cost: "",
    pin: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    let formatted = value;

    if (name === "pin") {
      const raw = value.replace(/\D/g, "").slice(0, 16);
      formatted = raw.match(/.{1,4}/g)?.join("-") || "";
    }

    setFormData({ ...formData, [name]: formatted });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted Form Data:", formData);
  };

  return (
    <div className="min-h-screen bg-[#0E0E0E] text-white flex items-center justify-center p-6">
      <form
        onSubmit={handleSubmit}
        className="bg-[#1A1A1A] p-8 rounded-xl shadow-xl w-full max-w-lg space-y-5"
      >
        <h2 className="text-3xl font-bold text-center text-[#92BC56] mb-4">
          Spidr Air Fryer Interest Form
        </h2>

        <input
          type="text"
          name="firstName"
          placeholder="First Name"
          value={formData.firstName}
          onChange={handleChange}
          className="input"
        />
        <input
          type="text"
          name="lastName"
          placeholder="Last Name"
          value={formData.lastName}
          onChange={handleChange}
          className="input"
        />
        <input
          type="tel"
          name="phone"
          placeholder="Phone Number"
          value={formData.phone}
          onChange={handleChange}
          className="input"
        />
        <input
          type="email"
          name="email"
          placeholder="Email Address"
          value={formData.email}
          onChange={handleChange}
          className="input"
        />
        <input
          type="number"
          name="cost"
          placeholder="Guess the Air Fryerâ€™s Cost ($)"
          value={formData.cost}
          onChange={handleChange}
          className="input"
        />
        <input
          type="text"
          name="pin"
          placeholder="####-####-####-####"
          value={formData.pin}
          onChange={handleChange}
          className="input"
        />

        <button
          type="submit"
          className="w-full py-3 bg-[#92BC56] text-black font-semibold rounded-lg hover:bg-[#7ba647] transition"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
